sys_script_client.script:
function onSubmit() {
   /* if (validacion()) {
        return true;
    } else {
        return false;
    };*/
    if (g_form.getValue("estatus") == 'NU') {
        var ga = g_form.getUniqueValue();
        var ab = new GlideAjax("mnx_scdb_valida_adjunto");
        ab.addParam("sysparm_name", "attachment");
        ab.addParam("sysparm_item_guid", ga);
		alert("g_scratchpad.isFormValid:"+ga);
        if (g_scratchpad.isFormValid) {
            return true;
        }
        var a = ab.getXML(checking);

        function checking(response) {
            var respuesta = response.responseXML.documentElement.getAttribute("answer");
			alert(respuesta);

			 g_form.addErrorMessage(respuesta);
            if (respuesta == 'yes') {
                g_scratchpad.isFormValid = true;
                g_form.submit("sysverb_insert");
            }
        }
        g_form.addErrorMessage('Adjuntar uno o más archivos');
        return false;
    }
}

function validacion() {
    if (g_form.getValue("estatus") == 'RE') {
        if (g_form.getValue("motivo_de_rechazo") == null ||
            g_form.getValue("motivo_de_rechazo") == '' ||
            g_form.getValue("motivo_de_rechazo").length <= 0) {
            g_form.addErrorMessage('Debe seleccionar motivo de rechazo');
            return false;
        }
    }
    return true;
}
