sys_ui_page.client_script:
function seleccionado() {
    g_form.setValue('n_mero_de_contrato', '19841289');
    g_form.setValue('titular', 'Ricardo Trejo Mendoza');

    var o = GlideModal.prototype.get('x_552735_tesorer_a_UI Buscar Contrato');
    o.destroy();
}

function buscar() {

    var userDetails = new GlideAjax('mnx_scdb_cliente_obtener_contratos'); //script include UserDetailsUtil

    userDetails.addParam("sysparm_name", "getValidation"); //this is the function within the script include
    //alert(document.getElementById("numeroContrato").value);
    userDetails.addParam("idContrato", document.getElementById("numeroContrato").value);
    userDetails.getXMLAnswer(function(response) {
    var jsonResponse = JSON.parse(response);		

	var html_message ="";
	if(jsonResponse.length >0){
	 html_message +="<div class='alert alert-success alert-dismissible col-xs-12' id='message'> chinga tu madre</div>";		
	 var html = "";
        html += "<table class='table table-hover'>";
        html += "<thead>";
        html += "<tr>";
        html += "<th style='text-align:center'>Seleccionar</th>";
        html += "<th style='text-align:center'>Número de contrato</th>";
        html += "<th style='text-align:center'>Titular</th>";
        html += "</tr>";
        html += "</thead>";
        html += "<tbody>";

        for (var i = 0; i < jsonResponse.length; i++) {
            html += "<tr>";
            html += "<td style='text-align:center'>";
			html += "<input type='radio' onclick='seleccionado()' id='male' name='gender' value='" + jsonResponse[i].idContrato + "'></td>";
            html += "<td style='text-align:center'>" + jsonResponse[i].idContrato + "</td>";
            html += "<td style='text-align:center'>" + jsonResponse[i].nombreTitular + "</td>";
            html += "</tr>";
        }

        html += "</tbody>";
        html += "</table>";

		document.getElementById("message").innerHTML = html_message;
		document.getElementById("printableArea").innerHTML = html;
	}else{
		html_message +="<div class='alert alert-success alert-dismissible col-xs-12 ' id='message'> chinga tu madre 2</div>";
		document.getElementById("message").innerHTML = html_message;
	}
    });


}
function cancelar() {
    var o = GlideModal.prototype.get('x_552735_tesorer_a_UI Buscar Contrato');
    o.destroy();
}
